import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import Top from "./Top";
const BoardDetail = () => {
  const location = useLocation();
  const { state } = location;
  const [showQR, setShowQR] = useState(false);

  const handleShowQR = () => {
    setShowQR(true);
  };

  if (!state) {
    return (
      <div>
        <Top></Top>
        <div>
          <h2>필동2가 범죄도시 보실분~</h2>
          <div>
            <div className="detail_title">
              <h3>참여자</h3>
            </div>
            <div className="detail_detail">
              <div>짱구(abc1234) 24/남</div>
              <div>짱구(abc1234) 24/남</div>
              <div>짱구(abc1234) 24/남</div>
            </div>
          </div>
          <div className="detail123">
            <div className="detail_title">
              <h3>모임장소</h3>
            </div>
            <div className="detail_detail">
              <div>대한극장 | 서울 중구 퇴계로 212</div>
            </div>
          </div>
          <div>
            <div className="detail_title">
              <h3>모임시간</h3>
            </div>
            <div className="detail_detail">
              <div>2024년 5월 12일 오전 11시 00분</div>
            </div>
          </div>
          <button onClick={handleShowQR}>QR 생성</button>
        </div>
      </div>
    );
  }

  const { id, title, content, time, views, comments, timeDiff } = state;

  return (
    <div>
      <h2>게시글 상세 페이지</h2>
      <p>게시글 ID: {id}</p>
      <p>제목: {title}</p>
      <p>내용: {content}</p>
      <p>작성 시간: {time}</p>
      <p>조회수: {views}</p>
      <p>댓글 수: {comments}</p>
      {/* 게시글 내용 등을 여기에 표시 */}
    </div>
  );
};

export default BoardDetail;
