// Detail.js

import React from "react";
import { useLocation } from "react-router-dom";

const Detail = () => {
  const location = useLocation();
  const { state } = location;

  if (!state) {
    return <div>게시글 정보가 없습니다.</div>;
  }

  const { id, title, content, time, views, comments } = state;

  return (
    <div>
      <h2>게시글 상세 페이지</h2>
      <p>게시글 ID: {id}</p>
      <p>제목: {title}</p>
      <p>내용: {content}</p>
      <p>작성 시간: {time}</p>
      <p>조회수: {views}</p>
      <p>댓글 수: {comments}</p>
      {/* 게시글 내용 등을 여기에 표시 */}
    </div>
  );
};

export default Detail;
