import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import Top from "./Top";
const BoardDetail = () => {
  const location = useLocation();
  const { state } = location;
  const [showQR, setShowQR] = useState(false);

  const handleShowQR = () => {
    setShowQR(true);
  };

  if (!state) {
    return (
      <div>
        <Top></Top>
        <div>
          <h2>게시글 상세 페이지</h2>
          <p>게시글 ID:</p>
          <p>제목: </p>
          <p>내용:</p>
          <p>작성 시간: </p>
          <p>조회수: </p>
          <p>댓글 수: </p>
          <button onClick={handleShowQR}>QR 생성</button>
        </div>
      </div>
    );
  }

  const { id, title, content, time, views, comments, timeDiff } = state;

  return (
    <div>
      <h2>게시글 상세 페이지</h2>
      <p>게시글 ID: {id}</p>
      <p>제목: {title}</p>
      <p>내용: {content}</p>
      <p>작성 시간: {time}</p>
      <p>조회수: {views}</p>
      <p>댓글 수: {comments}</p>
      {/* 게시글 내용 등을 여기에 표시 */}
    </div>
  );
};

export default BoardDetail;
